/**
 * 根据模型编码切换上下架状态
 * @param toggleReq 上下架切换请求
 * @return 是否成功
 */
@Override
public Result<String> toggleListed(RagConfigLlmModelListedToggleReq toggleReq) {
    log.info("根据模型编码切换上下架状态, modelCode: {}, listed: {}", 
        toggleReq.getModelCode(), toggleReq.getListed());

    // 根据模型编码查询
    String tenantId = UserContextHolder.getTenantId();
    LambdaQueryWrapper<RagConfigLlmModel> modelWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
    modelWrapper.eq(RagConfigLlmModel::getModelCode, toggleReq.getModelCode());
    modelWrapper.eq(RagConfigLlmModel::getTenantId, tenantId);
    RagConfigLlmModel record = ragConfigLlmModelMapper.selectOne(modelWrapper);

    Assert.notNull(record, "system.common.request.invalid");

    // 更新上下架状态
    RagConfigLlmModel ragConfigLlmModel = new RagConfigLlmModel();
    ragConfigLlmModel.setId(record.getId());
    ragConfigLlmModel.setListed(toggleReq.getListed());
    ragConfigLlmModel.setModifierTime(LocalDateTime.now());
    ragConfigLlmModel.setModifier(UserContextHolder.getCurrentUser().getCode());

    int ret = ragConfigLlmModelMapper.updateById(ragConfigLlmModel);
    if (ret > 0) {
        // 删除缓存
        this.clear(List.of(record.getModel(), record.getName()));
    }

    return ret > 0 
        ? Result.defaultSuccess(i18nMessage.resolveMessage("system.common.update.success")) 
        : Result.error(i18nMessage.resolveMessage("system.common.update.fail"));
}

/**
 * 根据模型编码切换上下架状态
