@Slf4j
@AllArgsConstructor
@Service
public class RagConfigLlmModelServiceImpl implements RagConfigLlmModelService {

    final RagConfigLlmModelMapper ragConfigLlmModelMapper;
    final StringRedisTemplate stringRedisTemplate;
    final I18nMessage i18nMessage;
    final ObjectMapper objectMapper = new ObjectMapper();
    final PluginVendorMapper pluginVendorMapper;
    final Gson gson = new GsonBuilder().setLenient().create();

    @Override
    public Result<List<RagConfigModelBrandResp>> providers() {
        return Result.data(Arrays.stream(ModelProviders.values()).filter(providers -> providers != ModelProviders.None).map(RagConfigModelBrandResp::of).toList());
    }

    @Override
    public List<UniversalEmbeddingConfig> findEmbeddingModelsByTenantId(String tenantId) {
        // 从数据库中查询
        LambdaQueryWrapper<RagConfigLlmModel> lambdaQueryWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
        // 查询可用的模型
        lambdaQueryWrapper.eq(RagConfigLlmModel::getOnlineStatus, ToggleStatusEnum.YES.getValue()).eq(RagConfigLlmModel::getTenantId,tenantId);
        //找嵌入模型
        lambdaQueryWrapper.eq(RagConfigLlmModel::getModelPurpose, LargeModelType.EMBEDDING_MODELS.name());
        // 由于不限制模型编码，因此可以有多个服务厂商来提供负载支持，随机获取一个
        List<RagConfigLlmModel> ragConfigLlmModels = ragConfigLlmModelMapper.selectList(lambdaQueryWrapper);
        if (CollUtil.isNotEmpty(ragConfigLlmModels)) {
            return ragConfigLlmModels.stream()
                    .map(model -> UniversalEmbeddingConfig.fromJson(model.getOtherConfig()))
                    .toList();
        }
        return List.of();
    }

    @Override
    public List<UniversalReRankerConfig> findReRankerModelsByTenantId(String tenantId) {
        // 从数据库中查询
        LambdaQueryWrapper<RagConfigLlmModel> lambdaQueryWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
        // 查询可用的模型
        lambdaQueryWrapper.eq(RagConfigLlmModel::getOnlineStatus, ToggleStatusEnum.YES.getValue()).eq(RagConfigLlmModel::getTenantId,tenantId);
        //找嵌入模型
        lambdaQueryWrapper.eq(RagConfigLlmModel::getModelPurpose, LargeModelType.RERANK_MODELS.name());
        // 由于不限制模型编码，因此可以有多个服务厂商来提供负载支持，随机获取一个
        List<RagConfigLlmModel> ragConfigLlmModels = ragConfigLlmModelMapper.selectList(lambdaQueryWrapper);
        if (CollUtil.isNotEmpty(ragConfigLlmModels)) {
            return ragConfigLlmModels.stream()
                    .map(model -> UniversalReRankerConfig.fromJson(model.getOtherConfig()))
                    .toList();
        }
        return List.of();
    }

    @Override
    public void clear(List<String> models) {
        Set<String> keys = new HashSet<>();
        for (String model : models) {
            keys.add(ModelSetting.cacheKey(model));
        }
        keys.add(CacheCns.CACHE_MODEL_AVAILABLE_SETTING);
        stringRedisTemplate.delete(keys);
    }

    @Override
    public void checkExists(String modelName,String tenantId) {
        LambdaQueryWrapper<RagConfigLlmModel> lambdaQueryWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
        lambdaQueryWrapper.eq(RagConfigLlmModel::getModel, modelName);
        lambdaQueryWrapper.eq(RagConfigLlmModel::getTenantId, tenantId);
        Assert.isFalse(ragConfigLlmModelMapper.exists(lambdaQueryWrapper), "模型编码[" + modelName + "]已经存在。");
    }

    @Override
    public Optional<RagConfigLlmModel> findByModelName(String modelName) {
        // 从数据库中查询
        LambdaQueryWrapper<RagConfigLlmModel> lambdaQueryWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
        // 查询可用的模型
        lambdaQueryWrapper.eq(RagConfigLlmModel::getOnlineStatus, ToggleStatusEnum.YES.getValue());
        // 模型编码或者名称都支持
        lambdaQueryWrapper.and(l -> l.eq(RagConfigLlmModel::getModel, modelName).or().eq(RagConfigLlmModel::getName, modelName));
        // 由于不限制模型编码，因此可以有多个服务厂商来提供负载支持，随机获取一个
        List<RagConfigLlmModel> ragConfigLlmModels = ragConfigLlmModelMapper.selectList(lambdaQueryWrapper);
        if (CollUtil.isNotEmpty(ragConfigLlmModels)){
            return Optional.of(ragConfigLlmModels.get(0));
        }
        return Optional.empty();
    }

    @Override
    public Map<String, UniversalEmbeddingConfig> findEmbeddingByModelNames(List<String> modelNames) {
        // 从数据库中查询
        LambdaQueryWrapper<RagConfigLlmModel> lambdaQueryWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
        // 查询可用的模型
        lambdaQueryWrapper.eq(RagConfigLlmModel::getOnlineStatus, ToggleStatusEnum.YES.getValue());
        // 模型编码或者名称都支持
        lambdaQueryWrapper.and(l -> l.in(RagConfigLlmModel::getModel, modelNames).or().in(RagConfigLlmModel::getName, modelNames));
        // 由于不限制模型编码，因此可以有多个服务厂商来提供负载支持，随机获取一个
        List<RagConfigLlmModel> ragConfigLlmModels = ragConfigLlmModelMapper.selectList(lambdaQueryWrapper);
        if (CollUtil.isEmpty(ragConfigLlmModels)){
            return Map.of();
        }
        Map<String, UniversalEmbeddingConfig> result = new HashMap<>();
        for (RagConfigLlmModel ragConfigLlmModel : ragConfigLlmModels) {
            UniversalEmbeddingConfig embeddingConfig = UniversalEmbeddingConfig.fromJson(ragConfigLlmModel.getOtherConfig());
            if (embeddingConfig==null){
                continue;
            }
            result.put(ragConfigLlmModel.getModel(), embeddingConfig);
            result.put(ragConfigLlmModel.getName(), embeddingConfig);
        }
        return result;
    }

    /**
     * 分页查询-大模型配置列表数据
     * @param ragConfigLlmModelQueryReq 查询条件Vo
     * @param pageNo 当前页码
     * @param pageSize 每页页码大小
     * @return 大模型配置列表
     */
    @Override
    public Pagination<RagConfigLlmModelResp> list(RagConfigLlmModelQueryReq ragConfigLlmModelQueryReq, Integer pageNo, Integer pageSize) {
        log.info("分页查询大模型配置列表,pageNo:{},pageSize:{}", pageNo, pageSize);
        log.info("分页QueryReq:{}", ragConfigLlmModelQueryReq.toString());
        // 获取租户编码
        String tenantId = UserContextHolder.getTenantId();
        Page<RagConfigLlmModel> ragConfigLlmModelPageNo = PageHelper.startPage(pageNo, pageSize);
        LambdaQueryWrapper<RagConfigLlmModel> lambdaQueryWrapper = Wrappers.lambdaQuery(RagConfigLlmModel.class);
        lambdaQueryWrapper.eq(RagConfigLlmModel::getTenantId, tenantId);
        lambdaQueryWrapper.eq(StrUtil.isNotBlank(ragConfigLlmModelQueryReq.getBrand()), RagConfigLlmModel::getBrand, ragConfigLlmModelQueryReq.getBrand());
        lambdaQueryWrapper.eq(StrUtil.isNotBlank(ragConfigLlmModelQueryReq.getModelPurpose()), RagConfigLlmModel::getModelPurpose, ragConfigLlmModelQueryReq.getModelPurpose());
        lambdaQueryWrapper.like(StrUtil.isNotBlank(ragConfigLlmModelQueryReq.getModel()), RagConfigLlmModel::getModel, ragConfigLlmModelQueryReq.getModel());
        lambdaQueryWrapper.like(StrUtil.isNotBlank(ragConfigLlmModelQueryReq.getName()), RagConfigLlmModel::getName, ragConfigLlmModelQueryReq.getName());
        lambdaQueryWrapper.eq(ragConfigLlmModelQueryReq.getVendorCode() != null, RagConfigLlmModel::getVendorCode, ragConfigLlmModelQueryReq.getVendorCode());
        lambdaQueryWrapper.eq(StrUtil.isNotBlank(ragConfigLlmModelQueryReq.getProvider()), RagConfigLlmModel::getProvider, ragConfigLlmModelQueryReq.getProvider());
        lambdaQueryWrapper.eq(ragConfigLlmModelQueryReq.getListed() != null, RagConfigLlmModel::getListed, ragConfigLlmModelQueryReq.getListed());
        List<RagConfigLlmModel> ragConfigLlmModelInfos = ragConfigLlmModelMapper.selectList(lambdaQueryWrapper);
        List<RagConfigLlmModelResp> ragConfigLlmModelRespInfos = new ArrayList<>();
        long count = ragConfigLlmModelPageNo.getTotal();
        if (CollectionUtil.isNotEmpty(ragConfigLlmModelInfos)) {
            ragConfigLlmModelRespInfos.addAll(ragConfigLlmModelInfos.stream().map(new RagConfigLlmModelApplyFunction(false)).toList());
        }
        return Pagination.pagination(ragConfigLlmModelRespInfos, count, pageNo, pageSize);
    }
    /**
     * 新增大模型配置记录
     * @param ragConfigLlmModelAddReq 新增大模型配置条件Vo
     * @return 是否新增成功
     */
    @Transactional(rollbackFor = Exception.class)
    @Override
    public Result<String> add(RagConfigLlmModelAddReq ragConfigLlmModelAddReq) {
        log.info("新增大模型配置数据,Vo:{}", ragConfigLlmModelAddReq.toString());
        // 获取用户信息
        SysUserInfo currentUser = UserContextHolder.getCurrentUser();
        // 获取租户编码
        String tenantId = currentUser.getTenantId();
        // 根据模型编码查询，如果存在则不允许新增 增加租户级别筛选
        this.checkExists(ragConfigLlmModelAddReq.getModel(),tenantId);
        RagConfigLlmModel ragConfigLlmModel = new RagConfigLlmModel();
        BeanUtils.copyProperties(ragConfigLlmModelAddReq, ragConfigLlmModel);
        if (StrUtil.isBlank(ragConfigLlmModelAddReq.getThinkingStatus())) {
            // 如果思考状态没有设置，则默认设置为否
            ragConfigLlmModel.setThinkingStatus(ToggleStatusEnum.NO.getValue());
        }
        // 生成唯一模型关联编码
        String modelCode = IdUtil.getSnowflakeNextIdStr();
        ragConfigLlmModel.setCreateTime(LocalDateTime.now());
        ragConfigLlmModel.setModifierTime(LocalDateTime.now());
        ragConfigLlmModel.setModelCode(modelCode);
        ragConfigLlmModel.setTenantId(tenantId);
        ragConfigLlmModel.setCreator(currentUser.getCode());
        ragConfigLlmModel.setModifier(currentUser.getCode());
        ModelProviders modelProviders = ModelProviders.of(ragConfigLlmModel.getBrand());
        if (modelProviders == ModelProviders.None) {
            throw new IllegalArgumentException("模型厂商不支持");
        }
        // 判断类型
        if (LargeModelType.EMBEDDING_MODELS.name().equals(ragConfigLlmModel.getModelPurpose())){
            // 处理为embedding的模型配置
            ragConfigLlmModel.setOtherConfig(ragConfigLlmModelAddReq.embeddingConfig().toJson());
        }else if (LargeModelType.RERANK_MODELS.name().equalsIgnoreCase(ragConfigLlmModel.getModelPurpose())) {
            // 处理为rerank的模型配置
            ragConfigLlmModel.setOtherConfig(ragConfigLlmModelAddReq.rankConfig().toJson());
        }else if (LargeModelType.OCR_MODELS.name().equalsIgnoreCase(ragConfigLlmModel.getModelPurpose())) {
            // 处理为大语言模型的配置
            ragConfigLlmModel.setOtherConfig(ragConfigLlmModelAddReq.ocrConfig().toJson());
        }
        int ret = ragConfigLlmModelMapper.insert(ragConfigLlmModel);
        return ret > 0 ? Result.defaultSuccess(i18nMessage.resolveMessage("system.common.add.success")) : Result.error(i18nMessage.resolveMessage("system.common.add.fail"));
    }

    /**
     * 更新大模型配置记录
     * @param ragConfigLlmModelUpdateReq 更新大模型配置条件Vo
     * @return 是否更新成功
     */
    @Override
    public Result<String> update(RagConfigLlmModelUpdateReq ragConfigLlmModelUpdateReq) {
        log.info("根据主键id修改大模型配置数据,Vo:{}", ragConfigLlmModelUpdateReq.toString());
        RagConfigLlmModel record = ragConfigLlmModelMapper.selectById(ragConfigLlmModelUpdateReq.getId());
        Assert.notNull(record, "system.common.request.invalid");
        if (!StrUtil.equalsIgnoreCase(record.getModel(), ragConfigLlmModelUpdateReq.getModel())) {
            // 模型编码发生了变更，去重
            this.checkExists(ragConfigLlmModelUpdateReq.getModel(),UserContextHolder.getTenantId());
        }
        ModelProviders modelProviders = ModelProviders.of(record.getBrand());
        if (modelProviders == ModelProviders.None) {
            throw new IllegalArgumentException("模型厂商不支持");
        }
        RagConfigLlmModel ragConfigLlmModel = new RagConfigLlmModel();
        BeanUtils.copyProperties(ragConfigLlmModelUpdateReq, ragConfigLlmModel);
        if (StrUtil.isBlank(ragConfigLlmModelUpdateReq.getThinkingStatus())) {
            // 如果思考状态没有设置，则默认设置为否
            ragConfigLlmModel.setThinkingStatus(ToggleStatusEnum.NO.getValue());
        }
        ragConfigLlmModel.setModifierTime(LocalDateTime.now());
        // 判断类型
        if (LargeModelType.EMBEDDING_MODELS.name().equals(ragConfigLlmModel.getModelPurpose())){
            // 处理为embedding的模型配置
            ragConfigLlmModel.setOtherConfig(ragConfigLlmModelUpdateReq.embeddingConfig().toJson());
        }
        else if (LargeModelType.RERANK_MODELS.name().equalsIgnoreCase(ragConfigLlmModel.getModelPurpose())) {
            // 处理为rerank的模型配置
            ragConfigLlmModel.setOtherConfig(ragConfigLlmModelUpdateReq.rankConfig().toJson());
        }else if (LargeModelType.OCR_MODELS.name().equalsIgnoreCase(ragConfigLlmModel.getModelPurpose())) {
            // 处理为大语言模型的配置
            ragConfigLlmModel.setOtherConfig(ragConfigLlmModelUpdateReq.ocrConfig().toJson());
        }
        int ret = ragConfigLlmModelMapper.updateById(ragConfigLlmModel);
        if (ret > 0) {
            // 删除缓存
            this.clear(List.of(record.getModel(), record.getName(), ragConfigLlmModelUpdateReq.getModel(), ragConfigLlmModelUpdateReq.getName()));
        }
        return ret > 0 ? Result.defaultSuccess(i18nMessage.resolveMessage("system.common.update.success")) : Result.error(i18nMessage.resolveMessage("system.common.update.fail"));
    }

    @Override
    public Result<String> toggle(RagConfigLlmModelUseToggleReq toggleReq) {
        RagConfigLlmModel record = ragConfigLlmModelMapper.selectById(toggleReq.getId());
        Assert.notNull(record, "system.common.request.invalid");
        RagConfigLlmModel ragConfigLlmModel = new RagConfigLlmModel();
        ragConfigLlmModel.setModifierTime(LocalDateTime.now());
        if (StrUtil.equalsIgnoreCase(ToggleStatusEnum.YES.getValue(), record.getOnlineStatus())) {
            ragConfigLlmModel.setOnlineStatus(ToggleStatusEnum.NO.getValue());
        } else {
            ragConfigLlmModel.setOnlineStatus(ToggleStatusEnum.YES.getValue());
        }
        ragConfigLlmModel.setId(toggleReq.getId());
        int ret = ragConfigLlmModelMapper.updateById(ragConfigLlmModel);
        if (ret > 0) {
            // 删除缓存
            this.clear(List.of(record.getModel(), record.getName()));
        }
        return ret > 0 ? Result.defaultSuccess(i18nMessage.resolveMessage("system.common.update.success")) : Result.error(i18nMessage.resolveMessage("system.common.update.fail"));
    }

    /**
     * 根据id查询大模型配置详情
     * @param id 大模型配置主键id
     * @return 大模型配置详情
     */
    @Override
    public Result<RagConfigLlmModelResp> queryById(Integer id) {
        log.info("根据主键id查询大模型配置详情,Id:{}", id);
        RagConfigLlmModel ragConfigLlmModel = ragConfigLlmModelMapper.selectById(id);
        Assert.notNull(ragConfigLlmModel, "system.common.request.invalid");
        RagConfigLlmModelResp ragConfigLlmModelResp = new RagConfigLlmModelApplyFunction(true).apply(ragConfigLlmModel);
        return Result.data(ragConfigLlmModelResp);
    }

    /**
     * 根据主键id批量删除大模型配置
     * @param ids 批量删除主键id集合
     * @return 是否删除成功
     */
    @Override
    public Result<String> delete(List<Integer> ids) {
        log.info("批量删除大模型配置,pageSize:{}", CollectionUtil.size(ids));
        int ret = ragConfigLlmModelMapper.deleteBatchIds(ids);
        return ret > 0 ? Result.defaultSuccess(i18nMessage.resolveMessage("system.common.delete.success")) : Result.error(i18nMessage.resolveMessage("system.common.delete.fail"));
    }

    /**
     * 根据主键id删除大模型配置
     * @param id 主键id
     * @return 是否删除成功
     */
