    @Override
    public ModelConnectivityTestResp testModelConnectivity(String modelName, String testMessage) {
        long startTime = System.currentTimeMillis();

        try {
            Optional<RagConfigLlmModel> modelOptional = findByModelName(modelName);
            if (modelOptional.isEmpty()) {
                return ModelConnectivityTestResp.failure(modelName, System.currentTimeMillis() - startTime, "未找到模型配置: " + modelName);
            }
            RagConfigLlmModel model = modelOptional.get();
            if (!"Y".equals(model.getOnlineStatus())) {
                return ModelConnectivityTestResp.failure(modelName, System.currentTimeMillis() - startTime, "模型已下线");
            }

            log.info("正在测试模型连通性, modelName: {}, testMessage: {}", modelName, testMessage);
            String result = callModelApi(model, testMessage);

            long responseTime = System.currentTimeMillis() - startTime;
            return ModelConnectivityTestResp.success(modelName, responseTime, result);

        } catch (Exception e) {
            long responseTime = System.currentTimeMillis() - startTime;
            log.error("测试模型连通性失败, modelName: {}, error: {}", modelName, e.getMessage(), e);
            return ModelConnectivityTestResp.failure(modelName, responseTime, "连接失败: " + e.getMessage());
        }
    }

    private String callModelApi(RagConfigLlmModel model, String testMessage) throws Exception {
        String apiUrl = model.getApiUrl();
        String[] secrets = model.getSecret().split(",");
        String apiKey = secrets[0].trim();

        HttpHeaders headers = new HttpHeaders();
        headers.set("Content-Type", "application/json");
        headers.set("Authorization", "Bearer " + apiKey);

        Map<String, Object> requestBody = new HashMap<>();
        requestBody.put("model", model.getModel());
        requestBody.put("enable_thinking", false);

        List<Map<String, String>> messages = new ArrayList<>();
        Map<String, String> message = new HashMap<>();
        message.put("role", "user");
        message.put("content", testMessage);
        messages.add(message);
        requestBody.put("messages", messages);

        Map<String, Object> parameters = new HashMap<>();
        parameters.put("max_tokens", 10); // 限制最大输出token数
        parameters.put("temperature", 0.1); // 降低随机性，加快响应
        requestBody.put("parameters", parameters);

        String jsonBody = objectMapper.writeValueAsString(requestBody);
        HttpEntity<String> request = new HttpEntity<>(jsonBody, headers);

        RestTemplate timeoutRestTemplate = new RestTemplate();
        SimpleClientHttpRequestFactory factory =  new SimpleClientHttpRequestFactory();
        factory.setConnectTimeout(5000); // 连接超时5秒
        factory.setReadTimeout(20000);   // 读取超时20秒
        timeoutRestTemplate.setRequestFactory(factory);
        timeoutRestTemplate.getMessageConverters().add(
            new org.springframework.http.converter.StringHttpMessageConverter(java.nio.charset.StandardCharsets.UTF_8));

        // 发送请求
        ResponseEntity<String> response = timeoutRestTemplate.exchange(
            apiUrl, HttpMethod.POST, request, String.class);

        if (response.getStatusCode().is2xxSuccessful()) {
            JsonNode responseNode = objectMapper.readTree(response.getBody());
            if (responseNode.has("choices") && responseNode.get("choices").isArray()
                && responseNode.get("choices").size() > 0) {
                JsonNode choice = responseNode.get("choices").get(0);
                if (choice.has("message") && choice.get("message").has("content")) {
                    return choice.get("message").get("content").asText();
                }
            }
            return "连通性测试成功 - API响应正常- 返回格式异常";
        } else {
            throw new RuntimeException("API调用失败，状态码: " + response.getStatusCode());
        }
    }

